mapper.select:
SELECT COUNT(*) FROM ( SELECT DISTINCT temp.alarm_id FROM (
SELECT DISTINCT ta.alarm_id, ts.status_code, ts.status_name, td.device_code, td.device_name, ta.alarm_info, ta.alarm_action, ta.action_user, ta.alarm_status, ta.alarm_time, ta.action_time, ta.send_flag, tm.thing_code, tm.thing_name, ta.thing_value, tar.area_id, tar.area_name FROM ${dbOldName}.tbl_alarm AS ta INNER JOIN ${dbName}.tbl_status AS ts ON ta.alarm_type = ts.status_code INNER JOIN ${dbName}.tbl_device AS td ON ta.device_code = td.device_code LEFT JOIN ${dbName}.tbl_monitor AS tm ON ta.thing_code = tm.thing_code LEFT JOIN ${dbName}.tbl_area AS tar ON td.area_id = tar.area_id WHERE 1 = 1
AND td.device_name LIKE CONCAT('%',CONCAT(#{alarm.device.deviceName},'%'))
AND ta.alarm_type = #{alarm.status.statusCode, jdbcType = VARCHAR}
AND ta.alarm_status = #{alarm.alarmStatus, jdbcType = VARCHAR}
AND TIMESTAMPDIFF(SECOND,#{alarm.beginAlarmTime},ta.alarm_time)>=0
AND TIMESTAMPDIFF(SECOND,ta.alarm_time,#{alarm.endAlarmTime})>=0
AND td.device_code IN
#{item}
UNION
SELECT DISTINCT ta.alarm_id FROM ${dbName}.tbl_alarm AS ta INNER JOIN ${dbName}.tbl_status AS ts ON ta.alarm_type = ts.status_code INNER JOIN ${dbName}.tbl_device AS td ON ta.device_code = td.device_code LEFT JOIN ${dbName}.tbl_monitor AS tm ON ta.thing_code = tm.thing_code LEFT JOIN ${dbName}.tbl_area AS tar ON td.area_id = tar.area_id WHERE 1 = 1
AND td.device_name LIKE CONCAT('%',CONCAT(#{alarm.device.deviceName},'%'))
AND ta.alarm_type = #{alarm.status.statusCode, jdbcType = VARCHAR}
AND ta.alarm_status = #{alarm.alarmStatus, jdbcType = VARCHAR}
AND TIMESTAMPDIFF(SECOND,#{alarm.beginAlarmTime},ta.alarm_time)>=0
AND TIMESTAMPDIFF(SECOND,ta.alarm_time,#{alarm.endAlarmTime})>=0
AND td.device_code IN
#{item}
) temp ) CTEMP

SELECT DISTINCT temp.alarm_id, temp.status_code, temp.status_name, temp.device_code, temp.device_name, temp.alarm_info, temp.alarm_action, temp.action_user, temp.alarm_status, temp.alarm_time, temp.action_time, temp.send_flag, temp.thing_code, temp.thing_name, temp.thing_value, temp.area_id, temp.area_name, temp.level_no FROM (
SELECT DISTINCT ta.alarm_id, ts.status_code, ts.status_name, td.device_code, td.device_name, ta.alarm_info, ta.alarm_action, ta.action_user, ta.alarm_status, ta.alarm_time, ta.action_time, ta.send_flag, tm.thing_code, tm.thing_name, ta.thing_value, tar.area_id, tar.area_name, ta.level_no FROM ${dbOldName}.tbl_alarm AS ta INNER JOIN ${dbName}.tbl_status AS ts ON ta.alarm_type = ts.status_code INNER JOIN ${dbName}.tbl_device AS td ON ta.device_code = td.device_code LEFT JOIN ${dbName}.tbl_monitor AS tm ON ta.thing_code = tm.thing_code LEFT JOIN ${dbName}.tbl_area AS tar ON td.area_id = tar.area_id WHERE 1 = 1
AND td.device_name LIKE CONCAT('%',CONCAT(#{alarm.device.deviceName},'%'))
AND ta.alarm_type = #{alarm.status.statusCode, jdbcType = VARCHAR}
AND ta.alarm_status = #{alarm.alarmStatus, jdbcType = VARCHAR}
AND TIMESTAMPDIFF(SECOND,#{alarm.beginAlarmTime},ta.alarm_time)>=0
AND TIMESTAMPDIFF(SECOND,ta.alarm_time,#{alarm.endAlarmTime})>=0
AND td.device_code IN
#{item}
UNION
SELECT DISTINCT ta.alarm_id, ts.status_code, ts.status_name, td.device_code, td.device_name, ta.alarm_info, ta.alarm_action, ta.action_user, ta.alarm_status, ta.alarm_time, ta.action_time, ta.send_flag, tm.thing_code, tm.thing_name, ta.thing_value, tar.area_id, tar.area_name, ta.level_no FROM ${dbName}.tbl_alarm AS ta INNER JOIN ${dbName}.tbl_status AS ts ON ta.alarm_type = ts.status_code INNER JOIN ${dbName}.tbl_device AS td ON ta.device_code = td.device_code LEFT JOIN ${dbName}.tbl_monitor AS tm ON ta.thing_code = tm.thing_code LEFT JOIN ${dbName}.tbl_area AS tar ON td.area_id = tar.area_id WHERE 1 = 1
AND td.device_name LIKE CONCAT('%',CONCAT(#{alarm.device.deviceName},'%'))
AND ta.alarm_type = #{alarm.status.statusCode, jdbcType = VARCHAR}
AND ta.alarm_status = #{alarm.alarmStatus, jdbcType = VARCHAR}
AND TIMESTAMPDIFF(SECOND,#{alarm.beginAlarmTime},ta.alarm_time)>=0
AND TIMESTAMPDIFF(SECOND,ta.alarm_time,#{alarm.endAlarmTime})>=0
AND td.device_code IN
#{item}
) temp ORDER BY temp.alarm_time DESC
LIMIT #{alarm.rowIndex, jdbcType = INTEGER},#{alarm.rowCount,jdbcType = INTEGER}

mapper.update:
UPDATE ${dbName}.tbl_alarm
SET action_time = #{alarm.actionTime}
	,action_user = #{alarm.actionUser}
	,alarm_status = #{alarm.alarmStatus}
	,alarm_action = #{alarm.alarmAction}
WHERE
alarm_id = #{alarm.alarmId, jdbcType = INTEGER}
