mapper.select:
SELECT COUNT(0) FROM tbl_area A,tbl_area_level C WHERE A.level_id = C.level_id AND A.parent_id IS NULL
AND A.area_id = #{area.areaId}
AND C.level_flag = #{area.areaLevel.levelFlag}
AND A.area_name LIKE CONCAT('%',CONCAT(#{area.areaName},'%'))

SELECT COUNT(0) FROM tbl_area A,tbl_area B,tbl_area_level C WHERE A.parent_id = B.area_id AND A.level_id = C.level_id
AND A.area_id = #{area.areaId}
AND C.level_flag = #{area.areaLevel.levelFlag}
AND A.area_name LIKE CONCAT('%',CONCAT(#{area.areaName},'%'))
AND A.parent_id = #{area.parentId}

SELECT A.area_id, A.area_name, A.parent_id, A.area_path, A.city_id, A.opt_user, A.opt_time, C.level_id, C.level_name, C.level_flag FROM tbl_area A,tbl_area_level C WHERE A.level_id = C.level_id AND A.parent_id IS NULL
AND A.area_id = #{area.areaId}
AND C.level_flag = #{area.areaLevel.levelFlag}
AND A.area_name LIKE CONCAT('%',CONCAT(#{area.areaName},'%'))
UNION
SELECT A.area_id, A.area_name, A.parent_id, A.area_path, A.city_id, A.opt_user, A.opt_time, C.level_id, C.level_name, C.level_flag FROM tbl_area A,tbl_area_level C WHERE A.level_id = C.level_id
AND A.area_id = #{area.areaId}
AND C.level_flag = #{area.areaLevel.levelFlag}
AND A.area_name LIKE CONCAT('%',CONCAT(#{area.areaName},'%'))
AND A.parent_id = #{area.parentId}
ORDER BY level_flag,area_name
LIMIT #{area.rowIndex, jdbcType = INTEGER},#{area.rowCount,jdbcType = INTEGER}

SELECT area_path
FROM tbl_area
WHERE tbl_area.area_id = #{areaid}

update.foreach:
UPDATE tbl_area SET
tbl_area.opt_user = #{item.optUser}
,tbl_area.opt_time = #{item.optTime}

	,tbl_area.area_name = #{item.areaName}


	,tbl_area.parent_id = #{item.parentId}


	,tbl_area.level_id = #{item.areaLevel.levelId}


	,tbl_area.area_path = #{item.areaPath}


	,tbl_area.city_id = #{item.cityId}

WHERE tbl_area.area_id = #{item.areaId, jdbcType = INTEGER}

mapper.delete:
DELETE FROM tbl_area
WHERE tbl_area.area_id IN

	#{item}

