SELECT COUNT(DISTINCT tms.storage_id) FROM ${dbName}.${deviceCode} tms INNER JOIN tbl_device_alarmset tda ON tms.thing_code = tda.thing_code AND tda.device_code = #{deviceCode} WHERE tms.update_type = #{updateType}
AND tms.thing_code = #{thingCode}
AND ( tda.max_value <= tms.thing_zsrtd OR tda.min_value >= tms.thing_zsrtd ) AND tda.data_flag = 2 AND DATE(tms.rtd_time) = DATE(#{selectTime})
AND ( tda.max_value <= tms.thing_rtd OR tda.min_value >= tms.thing_rtd ) AND tda.data_flag = 1 AND DATE(tms.rtd_time) = DATE(#{selectTime})
AND ( tda.max_value <= tms.thing_zsavg OR tda.min_value >= tms.thing_zsavg ) AND tda.data_flag = 2 AND DATE(tms.begin_time) = DATE(#{selectTime})
AND ( tda.max_value <= tms.thing_avg OR tda.min_value >= tms.thing_avg ) AND tda.data_flag = 1 AND DATE(tms.begin_time) = DATE(#{selectTime})

SELECT COUNT(DISTINCT tms.storage_id) FROM ${dbName}.${deviceCode} tms WHERE tms.update_type = #{updateType}
AND tms.thing_code = #{thingCode}
AND DATE(tms.rtd_time) = DATE(#{selectTime})
AND DATE(tms.begin_time) = DATE(#{selectTime})

SELECT COUNT(DISTINCT tms.storage_id) FROM ${dbName}.${deviceCode} tms INNER JOIN tbl_device_alarmset tda ON tms.thing_code = tda.thing_code AND tda.device_code = #{deviceCode} WHERE tms.update_type = #{updateType}
AND tms.thing_code = #{thingCode}
AND ( tda.max_value <= tms.thing_zsrtd OR tda.min_value >= tms.thing_zsrtd ) AND tda.data_flag = 2 AND MONTH(tms.rtd_time) = MONTH(#{selectTime})
AND ( tda.max_value <= tms.thing_rtd OR tda.min_value >= tms.thing_rtd ) AND tda.data_flag = 1 AND MONTH(tms.rtd_time) = MONTH(#{selectTime})
AND ( tda.max_value <= tms.thing_zsavg OR tda.min_value >= tms.thing_zsavg ) AND tda.data_flag = 2 AND MONTH(tms.begin_time) = MONTH(#{selectTime})
AND ( tda.max_value <= tms.thing_avg OR tda.min_value >= tms.thing_avg ) AND tda.data_flag = 1 AND MONTH(tms.begin_time) = MONTH(#{selectTime})

SELECT COUNT(DISTINCT tms.storage_id) FROM ${dbName}.${deviceCode} tms WHERE tms.update_type = #{updateType}
AND tms.thing_code = #{thingCode}
AND MONTH(tms.rtd_time) = MONTH(#{selectTime})
AND MONTH(tms.begin_time) = MONTH(#{selectTime})

SELECT COUNT(DISTINCT tms.storage_id) FROM ${dbName}.${deviceCode} tms INNER JOIN tbl_device_alarmset tda ON tms.thing_code = tda.thing_code AND tda.device_code = #{deviceCode} WHERE tms.update_type = #{updateType}
AND tms.thing_code = #{thingCode}
AND ( tda.max_value <= tms.thing_zsrtd OR tda.min_value >= tms.thing_zsrtd ) AND tda.data_flag = 2 AND YEAR(tms.rtd_time) = YEAR(#{selectTime})
AND ( tda.max_value <= tms.thing_rtd OR tda.min_value >= tms.thing_rtd ) AND tda.data_flag = 1 AND YEAR(tms.rtd_time) = YEAR(#{selectTime})
AND ( tda.max_value <= tms.thing_zsavg OR tda.min_value >= tms.thing_zsavg ) AND tda.data_flag = 2 AND YEAR(tms.begin_time) = YEAR(#{selectTime})
AND ( tda.max_value <= tms.thing_avg OR tda.min_value >= tms.thing_avg ) AND tda.data_flag = 1 AND YEAR(tms.begin_time) = YEAR(#{selectTime})

SELECT COUNT(DISTINCT tms.storage_id) FROM ${dbName}.${deviceCode} tms WHERE tms.update_type = #{updateType}
AND tms.thing_code = #{thingCode}
AND YEAR(tms.rtd_time) = YEAR(#{selectTime})
AND YEAR(tms.begin_time) = YEAR(#{selectTime})

SELECT COUNT(DISTINCT tms.storage_id) FROM ${dbName}.${deviceCode} tms WHERE tms.update_type = #{updateType}
AND tms.thing_code = #{thingCode}
AND TIMESTAMPDIFF(SECOND,#{beginTime},tms.rtd_time)>=0 AND TIMESTAMPDIFF(SECOND,tms.rtd_time,#{endTime})>=0
AND TIMESTAMPDIFF(SECOND,#{beginTime},tms.begin_time)>=0 AND TIMESTAMPDIFF(SECOND,tms.begin_time,#{endTime})>=0
