SELECT tdd.device_code, td.device_name, tdd.data_type, CASE WHEN tdd.data_type = '2011' THEN '实时数据' WHEN tdd.data_type = '2031' THEN '每日数据' WHEN tdd.data_type = '2051' THEN '分钟数据' WHEN tdd.data_type = '2061' THEN '小时数据' END AS data_type_name, tdd.thing_code, tm.thing_name, tdd.recent_data AS thing_value FROM tbl_device_data tdd INNER JOIN tbl_device td ON tdd.device_code = td.device_code
AND td.status_code IN ('N','NT')
INNER JOIN tbl_monitor tm ON tdd.thing_code = tm.thing_code WHERE tdd.thing_code = #{thingCode} AND tdd.data_type = #{dataType}
AND tdd.device_code IN
#{item}
ORDER BY tdd.recent_data ASC
ORDER BY tdd.recent_data DESC
LIMIT 0,#{limit, jdbcType = INTEGER}
LIMIT 0,5
