SELECT DISTINCT td.device_id AS point_id, ta.area_id AS area_id, ta.area_name AS area_name, td.device_code AS point_code, td.device_name AS point_name, td.device_x AS point_lng, td.device_y AS point_lat, ts.status_code AS point_status, ts.status_name AS point_status_name, tdd.recent_data AS point_data, bml.level_name AS point_level_name, bml.level_no AS point_level_no, bml.level_color AS point_level_color FROM tbl_device td INNER JOIN tbl_area ta ON td.area_id = ta.area_id LEFT JOIN tbl_status ts ON td.status_code = ts.status_code LEFT JOIN tbl_device_data tdd ON td.device_code = tdd.device_code LEFT JOIN tbl_monitor_level bml ON tdd.thing_code = bml.thing_code AND tdd.recent_data >= bml.level_min AND tdd.recent_data < bml.level_max WHERE td.device_type = '1' AND tdd.thing_code = #{thingCode} AND tdd.data_type = #{dataType}
AND td.device_code IN
#{item}
AND td.device_code IS NULL
AND bml.level_no = #{levelNo}
