mapper.select:
SELECT tbl_area.area_id,tbl_area.area_name,tbl_area_level.level_icon,tbl_area_level.level_flag FROM tbl_area,tbl_area_level WHERE tbl_area.level_id = tbl_area_level.level_id
AND tbl_area.parent_id = #{parentid}
AND tbl_area.parent_id IS NULL

SELECT COUNT(0) FROM tbl_area WHERE 1=1
AND tbl_area.parent_id = #{parentid}
AND tbl_area.parent_id IS NULL

SELECT COUNT(0) FROM tbl_device WHERE tbl_device.device_code IN ( SELECT device_code FROM tbl_access_device TD, tbl_user_ahr TUH WHERE TD.authority_code = TUH.authority_code AND TUH.user_code = #{usercode,jdbcType = VARCHAR} )
AND tbl_device.project_id = #{projectid,jdbcType = VARCHAR}
AND tbl_device.area_id IN (#{areaid,jdbcType = INTEGER})
AND tbl_device.device_name LIKE CONCAT('%',CONCAT(#{devicename,jdbcType = VARCHAR},'%'))
AND tbl_device.status_code NOT IN ('N','NT','Z','O')
AND tbl_device.status_code = #{statusCode}
AND tbl_device.status_code != #{nostatus,jdbcType = VARCHAR}

SELECT tbl_device.device_code,tbl_device.device_name, tbl_device.status_code FROM tbl_device WHERE tbl_device.device_code IN ( SELECT device_code FROM tbl_access_device TD, tbl_user_ahr TUH WHERE TD.authority_code = TUH.authority_code AND TUH.user_code = #{usercode,jdbcType = VARCHAR} )
AND tbl_device.project_id = #{projectid}
AND tbl_device.area_id IN (#{areaid,jdbcType = INTEGER})
AND tbl_device.device_name LIKE CONCAT('%',CONCAT(#{devicename,jdbcType = VARCHAR},'%'))
AND tbl_device.status_code NOT IN ('N','NT','Z','O')
AND tbl_device.status_code = #{status}
AND tbl_device.status_code != #{nostatus,jdbcType = VARCHAR}
ORDER BY tbl_device.area_id
